mapper.select:
SELECT id, title, description, completed, created_at as createdAt, updated_at as updatedAt
FROM tasks


        AND title LIKE CONCAT('%', #{params.title}, '%')


        AND description LIKE CONCAT('%', #{params.description}, '%')


        AND completed = #{params.completed}


        AND created_at >= #{params.startDate}


        AND created_at <= #{params.endDate}


LIMIT #{pageable.offset}, #{pageable.pageSize}

SELECT id, title, description, completed, created_at as createdAt, updated_at as updatedAt
FROM tasks
WHERE id = #{id}

SELECT COUNT(*)
FROM tasks


        AND title LIKE CONCAT('%', #{params.title}, '%')


        AND description LIKE CONCAT('%', #{params.description}, '%')


        AND completed = #{params.completed}


        AND created_at >= #{params.startDate}


        AND created_at <= #{params.endDate}
